package main

import \(
	"fmt"
	"log"

	"github.com/xuri/excelize/v2"
\)

func main\(\) {
	f, err := excelize.OpenFile\("AI Tool - GST_HSN Code summary_19.02.2025.xlsx"\)
	if err != nil {
		log.Fatal\(err\)
	}
	defer f.Close\(\)

	sheetName := f.GetSheetName\(0\)
	rows, err := f.GetRows\(sheetName\)
	if err != nil {
		log.Fatal\(err\)
	}

	// Print column N \(index 13\) for rows 6-15
	for i := 5; i < 15 && i < len\(rows\); i++ {
		row := rows[i]
		val := ""
		if len\(row\) > 13 {
			val = row[13]
		}

		// Also get the raw cell value
		cellRef := fmt.Sprintf\("N%d", i+1\)
		rawVal, _ := f.GetCellValue\(sheetName, cellRef\)
		rawFormula, _ := f.GetCellFormula\(sheetName, cellRef\)

		fmt.Printf\("Row %d: GetRows=%q  GetCellValue=%q  Formula=%q\\n", i+1, val, rawVal, rawFormula\)
	}
}
EOF)
